// Import @aibadgr/router
import { createRouter } from '@aibadgr/router';

// Configure router
const router = createRouter({
  providers: {
    aibadgr: { apiKey: $env.AIBADGR_API_KEY }
  }
});

// Get input text
const inputText = $input.first().json.text || 'Sample document text here';

// Summarize
const summary = await router.run({
  task: 'summarize',
  input: inputText,
  maxTokens: 150
});

// Extract key points
const extract = await router.run({
  task: 'extract',
  input: `Extract 3-5 key points from this text: ${inputText}`
});

// Calculate total cost
const totalCost = (summary.cost?.estimatedUsd || 0) + (extract.cost?.estimatedUsd || 0);

return [{
  json: {
    input: inputText,
    summary: summary.outputText,
    keyPoints: extract.outputText,
    metadata: {
      providers: {
        summary: summary.provider,
        extract: extract.provider
      },
      totalCost: totalCost,
      totalLatency: summary.latencyMs + extract.latencyMs
    }
  }
}];
